import React, { useState,useEffect, useMemo } from "react";
import { Form, Input, Button,Checkbox,Alert,notification } from 'antd';
import Header from '../commpont/UserHeader';
import Footer from '../commpont/Footer';
import '../style.css';

  
function Login() {
  const [email,setEmail]=useState("");
  const [password,setPassword]=useState("");

  const openNotificationWithIcon = (type,title,text) => {
    notification[type]({
      message: title,
      description:text,
    });
  };

  let handleSubmit = e => {
    e.preventDefault();
    if(email!="" && password!=""){
      $.ajax({
        type:"post",
        url:"http://sefdb02.qut.edu.au:3000/user/login",
        headers:{
          "accept":"application/json",
          'Content-Type': 'application/json'
        },
        data:JSON.stringify({
            email: email,
            password:password,
            longExpiry: false,
        }),
        success:function(data){
           openNotificationWithIcon('success','Login Tips',data.message);
           console.log(data);
           localStorage.setItem("bearerToken",data.bearerToken.token);
           localStorage.setItem("refreshToken",data.refreshToken.token);
           localStorage.setItem("email",email);
           setTimeout(function(){
            window.location.href="/";
          },2000);
        },
        error:function(data){
          openNotificationWithIcon('error','Login Tips',data.responseJSON.message);
        }
      });
    }else{
      openNotificationWithIcon('error','Login Tips','The password and Email address cannot be empty');
    }
  };

  return (
    <div>
      <Header/>
      <div className="form_box">
        <h2>Login</h2>
        <Form layout="inline" >
          <Form.Item>
              <Input
                placeholder="Email"
                onChange={e=>setEmail(e.target.value)}
              />
          </Form.Item>
          <Form.Item>
              <Input
                type="password"
                placeholder="Password"
                onChange={e=>setPassword(e.target.value)}
              />
          </Form.Item>
          <Form.Item>
            <Button type="primary" className="login-form-button" onClick={handleSubmit}>
              Log in
            </Button>
            Or <a href="/register">register now!</a>
          </Form.Item>
        </Form>
      </div>
      <Footer/>
    </div>
  );
}

export default Login;
