import React, { useState,useEffect, useMemo } from "react";
import { Form, Input, Button,Checkbox,Alert,Menu,notification,List, Avatar } from 'antd';
import { Link, useNavigate, useLocation } from "react-router-dom";
import '../style.css';

function Header() {
    const { SubMenu } = Menu;
    const location = useLocation();
    const navigate=useNavigate();
    const [bl,setBl]=useState(false);
    const [email,setEmail]=useState("");

    const openNotificationWithIcon = (type,title,text) => {
      notification[type]({
        message: title,
        description:text,
      });
    };

    const OpenProfile = () =>{
      window.location.href="/profile";
    }
  

    useEffect(()=>{
      var now_user=window.localStorage.getItem("now_user");
      if(now_user){
        now_user=JSON.parse(now_user);
        setBl(true);
        setEmail(now_user.email);
      }else{
        window.location.href="/login";
      }
    },[]);
    
    const loginOut=e=>{
      var refreshToken=localStorage.getItem("refreshToken");
      $.ajax({
        type:"post",
        url:"http://sefdb02.qut.edu.au:3000/user/logout",
        headers:{
          "accept":"application/json",
          'Content-Type': 'application/json'
        },
        data:JSON.stringify({
          refreshToken:refreshToken
        }),
        success:function(data){
          openNotificationWithIcon('success','User',data.message);
          localStorage.removeItem("refreshToken");
          localStorage.removeItem("bearerToken");
          localStorage.removeItem("email");
          setTimeout(function(){
            window.location.href="/login";
          },2000);
        },
        error:function(){
          openNotificationWithIcon('error','Login Tips','Login failure please log in again');
          localStorage.removeItem("refreshToken");
          localStorage.removeItem("bearerToken");
          localStorage.removeItem("email");
          setTimeout(function(){
            window.location.href="/login";
          },2000);
        }
      });
    }

    let menuClick = e => {
        if(e.key==1){
          navigate("/")
        }else if(e.key==2){
          navigate("/movie")
        }else if(e.key==3){
          navigate("/register")
        }else if(e.key==4){
          navigate("/login")
        }
    };

    return(
        <Menu  mode="horizontal" onClick={menuClick}>
            <span className="icon_title"> 
            Organic specialty products and small-scale farming
            </span>
            <Menu.Item key="1" >Home</Menu.Item>
            <Menu.Item key="2">Movie</Menu.Item>
            {bl?(
              <SubMenu title={"Hello!"+email} key="alipay" style={{position:"absolute",right: "1vw"}}>
                <Menu.Item onClick={OpenProfile}>My Profile</Menu.Item>
                <Menu.Item onClick={loginOut}>Login Out</Menu.Item>
              </SubMenu>
            ):(
              <Menu.Item key="3" >Register</Menu.Item>
            )}
            {bl?(
                <span></span>
            ):(
              <Menu.Item key="4" >Login</Menu.Item>
            )}

      </Menu>
    )
}

export default Header;
