import React, { useState,useEffect, useMemo } from "react";
import { Menu,notification,Input,Button, List, Avatar ,Select,Table, Divider, Tag  } from 'antd';
import { Link, useNavigate, useLocation } from "react-router-dom";
import '../style.css';
import Header from '../commpont/Header';
import Footer from '../commpont/Footer';


function People() {
  const { SubMenu } = Menu;
  const { Option } = Select;
  const [people,setPeople]=useState({});
  const navigate=useNavigate();
  const navigation = useLocation();
  const peopleId=navigation.search.split("=")[1];

  useEffect(()=>{
    var bearerToken=localStorage.getItem("bearerToken");
    $.ajax({
      type:"get",
      headers:{
        "accept":"application/json",
        "Authorization":"Bearer "+bearerToken,
      },
      url:"http://sefdb02.qut.edu.au:3000/people/"+peopleId,
      success:function(data){
        console.log(data);
        setPeople(data);
      },
      error:function(data){
        openNotificationWithIcon('error','Error',"Unknown error occurred");
      }
    });
  },[]);
  
  const openDetail = e => {
    console.log(e.target.name);
    navigate("/details?imdbId="+e.target.name);
  }

  const columns = [
    {
      title: 'MovieName',
      dataIndex: 'movieName',
      key: 'movieName',
      render: (text,key) => <a onClick={openDetail} name={key.movieId}>{text}</a>,
    },
    {
      title: 'Category',
      dataIndex: 'category',
      key: 'category',
    },
    {
      title: 'imdb Rating',
      dataIndex: 'imdbRating',
      key: 'imdbRating',
    },
  ];

  const openNotificationWithIcon = (type,title,text) => {
    notification[type]({
      message: title,
      description:text,
    });
  };


 

  return (
    <div>
      <Header/>
      <div className="details_box">
        <h1>{people.name}</h1>
        <h3>{people.birthYear} - {people.deathYear}</h3>
        <Table columns={columns} dataSource={people.roles}/>
      </div>

      <Footer/>
    </div>
  );
}

export default People;
