import React, { useState,useEffect, useMemo } from "react";
import { Form, Input, Button,Checkbox,Alert,Menu,notification,List, Avatar } from 'antd';
import { Link, useNavigate, useLocation } from "react-router-dom";
import Header from '../commpont/Header';
import Footer from '../commpont/Footer';
import '../style.css';

  
function Home() {
  const [email,setEmail]=useState("");
  const [password,setPassword]=useState("");
  const navigate = useNavigate();


  const openNotificationWithIcon = (type,title,text) => {
    notification[type]({
      message: title,
      description:text,
    });
  };

  // $.ajax({
  //   type:"get",
  //   url:"http://sefdb02.qut.edu.au:3000/movies/search?title=s&page=1",
  //   success:function(data){
  //      console.log(data);
  //   },
  //   error:function(data){
  //     openNotificationWithIcon('error','Login Tips',data.responseJSON.message);
  //   }
  // });

  let handleSubmit = e => {
    e.preventDefault();
    if(email!="" && password!=""){
      $.ajax({
        type:"post",
        url:"http://localhost:3007/login",
        data:{
            email: email,
            password:password
        },
        success:function(data){
           openNotificationWithIcon('success','Login Tips',data.message);
           localStorage.setItem("token",data.token);
           localStorage.setItem("access_token",data.access_token);
           setTimeout(function(){
            window.location.href="/search";
          },2000);
        },
        error:function(data){
          openNotificationWithIcon('error','Login Tips',data.responseJSON.message);
        }
      });
    }else{
      openNotificationWithIcon('error','Login Tips','The password and Email address cannot be empty');
    }
  };

  return (
    <div>
      <Header/>

      <div className="home_body">
        <h1>Movie Searching Website</h1>
        <div className="add_body">
          <h2>Add Bargain</h2>
          <Form layout="inline" >
          <Form.Item>
              <Input
                placeholder="Bargain Name"
                onChange={e=>setEmail(e.target.value)}
              />
          </Form.Item>
          <Form.Item>
              <Input
                placeholder="Bargain p"
                onChange={e=>setPassword(e.target.value)}
              />
          </Form.Item>
          <Form.Item>
            <Button type="primary" className="login-form-button" onClick={handleSubmit}>
              Log in
            </Button>
            Or <a href="/register">register now!</a>
          </Form.Item>
        </Form>
        </div>
      </div>

      <Footer/>
    </div>
  );
}

export default Home;
