import React, { useState,useEffect, useMemo } from "react";
import { Form, Input, Button,Checkbox,notification } from 'antd';
import '../style.css';
import Header from '../commpont/Header';
import Footer from '../commpont/Footer';

function Register() {
  const [email,setEmail]=useState("");
  const [username,setUsername]=useState("");
  const [password,setPassword]=useState("");
  const [repassword,setRepassword]=useState("");


  const openNotificationWithIcon = (type,title,text) => {
    notification[type]({
      message: title,
      description:text,
    });
  };

  let handleSubmit = e => {
    e.preventDefault();
    if(email!="" && password!="" && repassword!=""){
      if(repassword==password){
        $.ajax({
          type:"post",
          url:"http://sefdb02.qut.edu.au:3000/user/register",
          headers:{
            "accept":"application/json",
            'Content-Type': 'application/json'
          },
          data:JSON.stringify({
              email: email,
              password:password,
          }),
          success:function(data){
            openNotificationWithIcon('success','Register Tips',data.message);
            setTimeout(function(){
              window.location.href="/login";
            },2000);
          },
          error:function(data){
            openNotificationWithIcon('error','Register Tips',data.responseJSON.message);
          }
        });
      }else{
        openNotificationWithIcon('error','Register Tips','The two passwords must be the same!');
      }
    }else{
      openNotificationWithIcon('error','Register Tips','The registration information cannot be empty');
    }
  };

  return (
    <div>
      <Header/>
      <div className="form_box">
        <h2>Register</h2>
        <Form layout="inline">
          <Form.Item>
              <Input
                placeholder="Email"
                onChange={e=>setEmail(e.target.value)}
              />
          </Form.Item>
          <Form.Item>
              <Input
                type="password"
                placeholder="Password"
                onChange={e=>setPassword(e.target.value)}
              />
          </Form.Item>
          <Form.Item>
              <Input
                type="password"
                placeholder="Re-Password"
                onChange={e=>setRepassword(e.target.value)}
              />
          </Form.Item>
          <Form.Item>
            <Button type="primary" className="register-form-button"  onClick={handleSubmit}>
              Register
            </Button>
            Or <a href="/">Back Login!</a>
          </Form.Item>
        </Form>
      </div>
      <Footer/>
    </div>
  );
}

export default Register;
